// import NavBar from './components/NavBar.vue';
import FooterBar from './components/FooterBar.vue';
import Calendar from './components/Calendar.vue';
import axios from 'axios';
import { gsap } from 'gsap';
import { SplitText } from 'gsap/SplitText';
import { ScrollTrigger } from 'gsap/ScrollTrigger';

// 註冊 SplitText 和 ScrollTrigger 插件
gsap.registerPlugin(SplitText, ScrollTrigger);
export default {
  name: 'App',
  components: {
    FooterBar,
    Calendar
  },
  data() {
    return {
      form: {
        name: '',
        email: '',
        message: ''
      },
      schedule: [{
        time: '10:00 - 11:00',
        title: '開幕式',
        location: '主舞台'
      }, {
        time: '11:15 - 12:00',
        title: '主題演講：未來科技',
        location: '會議廳A'
      }, {
        time: '13:00 - 14:30',
        title: '工作坊：互動設計',
        location: '教室B'
      }, {
        time: '15:00 - 16:00',
        title: '圓桌論壇',
        location: '會議廳A'
      }, {
        time: '16:30 - 17:00',
        title: '閉幕式',
        location: '主舞台'
      }],
      showLiveDrawer: false,
      showDrawer: false,
      showLectureDrawer: false
    };
  },
  mounted() {
    this.initTextAnimation();
    this.fetchDatabaseData();
    this.pinBanner();
    this.initSunAnimation();
    this.initCloud1Animation();
  },
  methods: {
    // 取得資料庫資料並在 console 顯示
    async fetchDatabaseData() {
      try {
        const response = await axios.get('http://localhost:3001/api/data');
        console.log(response.data);
        // console.log('📋 資料筆數:', response.data.length);
        // console.log('🔍 詳細資料:', JSON.stringify(response.data, null, 2));
        return response.data;
      } catch (error) {
        console.error('❌ 取得資料庫資料失敗:', error);
        return [];
      }
    },
    scrollToSection(sectionId) {
      const element = document.getElementById(sectionId);
      if (element) {
        element.scrollIntoView({
          behavior: 'smooth'
        });
      }
    },
    submitForm() {
      alert('感謝您的訊息！我們會盡快回覆您。');
      this.form = {
        name: '',
        email: '',
        message: ''
      };
    },
    initTextAnimation() {
      // 分割 FUTURE VISION LAB 2025 標題
      const splitTitle = document.querySelector('.split');
      if (splitTitle) {
        const splitText = new SplitText(splitTitle, {
          type: "chars, words",
          charsClass: "char",
          wordsClass: "word"
        });

        // 設置初始狀態
        gsap.set(splitText.chars, {
          opacity: 0,
          y: 50,
          rotationX: -90
        });

        // 動畫效果
        gsap.to(splitText.chars, {
          opacity: 1,
          y: 0,
          rotationX: 0,
          duration: 0.8,
          stagger: 0.05,
          ease: "back.out(1.7)"
        });
      }

      // 分割首頁標題文字
      const heroTitle = document.querySelector('.hero-title');
      if (heroTitle) {
        const splitTitle = new SplitText(heroTitle, {
          type: "chars, words",
          charsClass: "char",
          wordsClass: "word"
        });

        // 設置初始狀態
        gsap.set(splitTitle.chars, {
          opacity: 0,
          y: 50,
          rotationX: -90
        });

        // 動畫效果
        gsap.to(splitTitle.chars, {
          opacity: 1,
          y: 0,
          rotationX: 0,
          duration: 0.8,
          stagger: 0.05,
          ease: "back.out(1.7)"
        });
      }

      // 分割副標題
      const heroSubtitle = document.querySelector('.hero-subtitle');
      if (heroSubtitle) {
        const splitSubtitle = new SplitText(heroSubtitle, {
          type: "words",
          wordsClass: "word"
        });
        gsap.set(splitSubtitle.words, {
          opacity: 0,
          y: 30
        });
        gsap.to(splitSubtitle.words, {
          opacity: 1,
          y: 0,
          duration: 0.6,
          stagger: 0.1,
          delay: 0.5,
          ease: "power2.out"
        });
      }

      // ScrollTrigger 動畫 - 節目時間表
      gsap.fromTo('#schedule .section-title, #schedule .schedule-table', {
        opacity: 0,
        y: 50
      }, {
        opacity: 1,
        y: 0,
        duration: 0.8,
        stagger: 0.2,
        ease: "power2.out",
        scrollTrigger: {
          trigger: "#schedule",
          start: "top 80%",
          end: "bottom 20%",
          toggleActions: "play none none reverse"
        }
      });

      // ScrollTrigger 動畫 - 關於DOME
      gsap.fromTo('#services .section-title, #services .service-card', {
        opacity: 0,
        y: 50
      }, {
        opacity: 1,
        y: 0,
        duration: 0.8,
        stagger: 0.2,
        ease: "power2.out",
        scrollTrigger: {
          trigger: "#services",
          start: "top 80%",
          end: "bottom 20%",
          toggleActions: "play none none reverse"
        }
      });

      // ScrollTrigger 動畫 - 作品集
      gsap.fromTo('#portfolio .section-title, #portfolio .portfolio-item', {
        opacity: 0,
        y: 50
      }, {
        opacity: 1,
        y: 0,
        duration: 0.8,
        stagger: 0.2,
        ease: "power2.out",
        scrollTrigger: {
          trigger: "#portfolio",
          start: "top 80%",
          end: "bottom 20%",
          toggleActions: "play none none reverse"
        }
      });

      // ScrollTrigger 動畫 - 製作團隊
      gsap.fromTo('#team .section-title, #team .team-item', {
        opacity: 0,
        y: 30
      }, {
        opacity: 1,
        y: 0,
        duration: 0.6,
        stagger: 0.1,
        ease: "power2.out",
        scrollTrigger: {
          trigger: "#team",
          start: "top 80%",
          end: "bottom 20%",
          toggleActions: "play none none reverse"
        }
      });

      // ScrollTrigger 動畫 - 聯絡我們
      gsap.fromTo('#contact .section-title, #contact .contact-info, #contact .contact-form', {
        opacity: 0,
        y: 50
      }, {
        opacity: 1,
        y: 0,
        duration: 0.8,
        stagger: 0.3,
        ease: "power2.out",
        scrollTrigger: {
          trigger: "#contact",
          start: "top 80%",
          end: "bottom 20%",
          toggleActions: "play none none reverse"
        }
      });
    },
    pinBanner() {
      gsap.registerPlugin(ScrollTrigger);
      gsap.timeline({
        scrollTrigger: {
          trigger: ".hero-banner-img",
          start: "top top",
          end: "+=1200",
          pin: "#home"
        }
      });
    },
    initSunAnimation() {
      // 設置太陽圖片的初始位置（在背景圖片下方）和透明度
      gsap.set(this.$refs.sunImg, {
        y: '100%',
        opacity: 1
      });

      // // 創建太陽上升的動畫
      gsap.to(this.$refs.sunImg, {
        y: '0%',
        opacity: 1,
        duration: 2,
        ease: "power2.out"
      });
    },
    initCloud1Animation() {
      gsap.set(this.$refs.cloud1Img), {
        x: '100%',
        opacity: 1
      };
      gsap.to(this.$refs.cloud1Img), {
        x: '0%',
        opacity: 1,
        duration: 2,
        ease: "power2.out"
      };
    }
  }
};
