import Vue from 'vue';
import VueRouter from 'vue-router';
import PortfolioDetail from '../views/PortfolioDetil.vue';
import Home from '@/App.vue'; // 假設首頁也在 App.vue
// Vue.use(VueRouter);

const routes = [
  {
    path: '/',
    name: 'Home',
    component: Home
  },
  {
    path: '/portfolio/:id',
    name: 'PortfolioDetail',
    component: PortfolioDetail
  }
];

const router = new VueRouter({
  mode:'history',
  base:process.env.BASE_URL,
  routes
});

export default router;
