// router/index.js (正確 Vue 3 寫法)
import { createRouter, createWebHistory } from 'vue-router';
import PortfolioDetil from '../views/PortfolioDetil.vue';
import HomeView from '../views/Home.vue';
const routes = [
  {
    path: '/',
    name: 'home',
    component: () => import(HomeView) // 注意檔名也建議改正
  },
  {
    path: '/portfolioDetil',
    name: 'PortfolioDetil',
    component: () => import(PortfolioDetil) // 注意檔名也建議改正
  }
];

const router = createRouter({
  history: createWebHistory(),
  routes
});

export default router;
