    runTime() {
      const imageContainer = document.getElementById("imageContiner");
      if (!imageContainer) return;
      this.canvasHeight = imageContainer.offsetHeight;
      this.canvasWidth = imageContainer.offsetWidth;
      this.canvasHeight = document.getElementById("imageContiner").offsetHeight;
      this.canvasWidth = document.getElementById("imageContiner").offsetWidth;
      this.createScene();
      this.createLights(); // 建立燈光
      this.createObject();
      this.windowResize();
      this.renderScene();
    },
    createScene() {
      this.scene = new THREE.Scene();
      this.camera = new THREE.PerspectiveCamera(45, this.canvasWidth / this.canvasHeight, 1, 1000);
      // this.camera.position.set(150,150,150);
      this.camera.position.z = 5;
      this.camera.lookAt(0, 0, 0);
      const axesHelper = new THREE.AxesHelper(100); // 參數代表軸長度
      this.scene.add(axesHelper);
      this.scene.add(this.camera);
      this.renderer = new THREE.WebGLRenderer();
      this.renderer.setSize(this.canvasWidth, this.canvasHeight); // 設定渲染器大小跟 canvas 一樣
      this.renderer.setPixelRatio(window.devicePixelRatio); // 設定渲染器比例跟螢幕一樣
      document.getElementById("imageContiner").appendChild(this.renderer.domElement); // 把渲染器加到 #th
    },
    createLights() {
      const Light = new THREE.HemisphereLight(0xffffff, 0x080820, 0.8); // 設定環境光源
      this.scene.add(Light); // 把光源加到場景
    },
    createObject() {
      const geometry = new THREE.SphereGeometry(1, 60, 60); // 建立長闊高都是 100，每邊由 5 個多邊形組成的方體
      const cubeMaterial = new THREE.MeshLambertMaterial({
        // 建立材質
        color: 0x00ff00 // 顏色
      });
      this.sphere = new THREE.Mesh(geometry, cubeMaterial); // 設定box的形狀及材質                        // 設定box的位置
      this.scene.add(this.sphere);

      // this.sphere.position.set(60, 0, -50);                                       // 把box加到場景
    },
    windowResize() {
      this.canvasHeight = document.getElementById("imageContiner")?.offsetHeight || window.innerHeight;
      this.canvasWidth = document.getElementById("imageContiner")?.offsetWidth || window.innerWidth;
      this.renderer.setSize(this.canvasWidth, this.canvasHeight); // 重設長闊
      this.camera.aspect = this.canvasWidth / this.canvasHeight;
      this.camera.updateProjectionMatrix(); // 更新鏡頭視點
    },
    renderScene() {
      requestAnimationFrame(this.renderScene); // 產生動畫  
      this.box.rotation.y += 0.005; // 令box的Y軸在每格動畫中轉動一點點
      this.renderer.render(this.scene, this.camera);
