        duration: 3,
        ease: "power2.out"
      });
    },
    scrollServices(direction) {
      const slider = this.$refs.servicesSlider;
      const card = slider.querySelector('.service-card');
      if (slider && card) {
        const scrollAmount = card.offsetWidth + 24; // 24px gap
        slider.scrollBy({
          left: direction * scrollAmount,
          behavior: 'smooth'
        });
      }
    },
    initServicesSliderScroll() {
      this.$nextTick(() => {
        const slider = this.$refs.servicesSlider;
        if (!slider) return;
        const container = slider.parentElement;
        const cardWidth = 320; // 卡片寬度
        const gap = 24; // 間距
        const containerPadding = 40; // 容器的左右padding總和

        // 計算容器實際可用寬度
        const availableWidth = container.offsetWidth - containerPadding;
        const cardsPerView = Math.floor(availableWidth / (cardWidth + gap)); // 可視卡片數量
        const totalCards = slider.children.length;

        // 計算最大可滑動距離，確保最後一張卡片完整顯示
        const maxScroll = Math.max(0, (totalCards - cardsPerView) * (cardWidth + gap));
        console.log('Container width:', container.offsetWidth);
        console.log('Available width:', availableWidth);
        console.log('Cards per view:', cardsPerView);
        console.log('Total cards:', totalCards);
        console.log('Max scroll:', maxScroll);
        if (maxScroll <= 0) return;
        gsap.set(slider, {
          x: 0
        });
        gsap.to(slider, {
          x: -maxScroll,
          // 內容往左滑
          ease: 'none',
          scrollTrigger: {
            trigger: '#services',
            start: 'top top',
            end: '+=' + maxScroll,
            scrub: 1.2,
            pin: true,
            anticipatePin: 1,
            markers: true
          }
        });
      });
    },
